CREATE TABLE member (id INT UNSIGNED AUTO_INCREMENT, hashed_password VARCHAR(32) NOT NULL, name VARCHAR(128) NOT NULL, name_roman VARCHAR(128) NOT NULL, gender TINYINT UNSIGNED NOT NULL, birth_date DATE NOT NULL, language_code VARCHAR(10) NOT NULL, registration_code TINYINT UNSIGNED NOT NULL, mail_address VARCHAR(100) NOT NULL, status TINYINT NOT NULL, deleted_at DATETIME, created_at DATETIME NOT NULL, created_by INT UNSIGNED, updated_at DATETIME NOT NULL, updated_by INT UNSIGNED, PRIMARY KEY(id)) ENGINE = INNODB;
